let includeTags=document.getElementsByTagName("include"),extendTags=document.getElementsByTagName("extends");for(let e of includeTags)fetch(e.attributes.src.value).then(e=>e.text()).then(t=>{e.innerHTML=t}).catch(e=>{console.warn(e)});for(let e of extendTags)fetch(e.attributes.src.value).then(e=>e.text()).then(t=>{let n=document.createElement("html");n.innerHTML=t;let a=document.createElement("html");a.innerHTML=n.outerHTML;let r=document.createElement("html");r.innerHTML=a.innerHTML,document.head.innerHTML=r.getElementsByTagName("head")[0].innerHTML,e.innerHTML=r.getElementsByTagName("body")[0].innerHTML;for(var l=document.createDocumentFragment();e.firstChild;)l.appendChild(e.firstChild);e.parentNode.replaceChild(l,e)}).catch(e=>{console.warn(e)});